INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('21', '21', 'en_us', 'About', NULL, '', '', '', '', '', '', '<h3>Gbenga Biobaku & Co. (GBC) is a leading law firm in Nigeria with primary practice in the business scene.</h3>\r\n\r\n<p>For over 20years, our Firm has demonstrated its expertise in handling challenging assignments for the most interesting clients. We have achieved extraordinary results with a team of intensely focused members with a focus on solving our clients\' most important problems.</p>\r\n\r\n\r\n\r\n<p>Building strong client relationships is the compass of our business strategy. With diverse experience and exceptional professionals, we have continually delivered quality to our clients through innovative legal solutions.</p>\r\n\r\n\r\n\r\n<p>Our clients range from multinationals, oil & energy firms, financial institutions and emerging companies. We also advise governments and public bodies.</p>', NULL, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2015-11-17 12:09:53', '2015-11-17 12:40:01', 'd33c635e-7a9c-4b56-8800-40334545a4d8');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('22', '22', 'en_us', 'Gbenga Biobaku', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Managing Partner', '<p>Gbenga has over 2 decades of legal practice experience in corporate / commercial law predominantly in the Oil and Gas Industry. Previously at Jackson, Etti & Edu, where he was the head of the energy & corporate department for over 5 years. Prior to joining Jackson, Etti & Edu, Gbenga was a member of the energy and corporate/commercial practice group at Irving and Bonnar, Nigeria’s oldest law firm. Whilst at Irving & Bonnar, Gbenga worked on a variety of energy, corporate and commercial transactions for both domestic and international clients.</p>', '[{\"col1\":\"University of Ife (LLB); 1986\"},{\"col1\":\"Nigerian Bar Association 1987\"}]', '[{\"col1\":\"Foreign Investments\"},{\"col1\":\"Joint Ventures\"},{\"col1\":\"Acquisitions & Disposals\"},{\"col1\":\"Corporate Restructuring\"},{\"col1\":\"Project Development & Finance\"}]', '[{\"col1\":\"Nigerian Bar Association\"},{\"col1\":\"Energy & Natural Resources Section (SERL) of the International Bar Association\"},{\"col1\":\"Association of International Petroleum Negotiators(\\\"AIPN\\\")\"}]', '[{\"col1\":\"Advising a multinational exploration and production company in relation to a proposed LNG Project in the Niger-Delta region of Nigeria.\"},{\"col1\":\"Advising on the $785 Million Oso Field Development Project jointly owned by Mobil Producing Nigeria Unlimited and the Nigerian National Petroleum Corporation (NNPC), and partly financed by equity from the sponsors, and debt capital from international institutions, such as, the World Bank, International Finance Corporation (IFC), US Export Import Bank, Japan Export Import Bank and the European Investment Bank.\"},{\"col1\":\"Advising and negotiating the project financing, documentation and designing of the investment vehicle of the US$810million Oso-NGL Project, a gas utilisation project, which was sequel to the Oso-Field Development Project, and partly financed by equity from the sponsors and debt capital by a number of institutional investors in the United States and Europe.\"},{\"col1\":\"Advising the Norwegian Exploration & Production Company, Statoil, in respect of several Production Sharing Contracts with the Nigerian National Petroleum Corporation (NNPC), and assisting with the negotiation.\"},{\"col1\":\"Advising an international oil company in its bid for an oil block in the Nigeria\\/Sao Tome and Principe JDZ 2003 Licensing Round and conducting a detailed analysis of the Treaty and the petroleum, fiscal and contractual regime.\"},{\"col1\":\"Advising Sonatrach, the Algerian state oil company in connection with its bid for an oil block in the 2005 Licensing Round and providing advice on the bidding regulations as well as detailed analysis of the petroleum, fiscal and contractual regime.\"},{\"col1\":\"In collaboration with lawyers in a UK law firm, acting for and advising a group of International Banks on aspects of the financing of the Eleme Petrochemical Plant.\"},{\"col1\":\"Advising Statoil on the establishment of several subsidiary companies in Nigeria, including processing and obtaining of all pre-investment and regulatory approvals.\"},{\"col1\":\"Advising and assisting Consolidated Petroleum PTY, a South African Company on the farm-in arrangements into a marginal field including the drafting and review of the transaction documents including the Purchase Agreement, Farm-in Agreement, Joint Operating Agreement and the Deed of Assignment.\"},{\"col1\":\"Advising on several acquisitions and mergers, involving oil service companies, including the acquisition by Baker Hughes of a minority interest in a Nigerian Oil Service Company.\"},{\"col1\":\"Advising Baker Hughes on all legal aspects of the construction and development of a major facility in Port Harcourt, from negotiation of the long lease for the property to negotiating and rendering advise on the construction contract, advising on Town Planning regulatory issues as well as Environmental issues, and undertaking the registration and perfection of the long lease.\"},{\"col1\":\"Advising Skye Bank Plc in relation to a USD75million syndicated petroleum products receivables pre-financing facility.\"},{\"col1\":\"Advising TABA Nigeria Limited in respect of an Engineering Procurement and Construction (\\u201cEPC\\u201d) contract with NNPC relating to a power plant and advising and assisting with the structuring and documentation of the various subcontracting and the financing arrangements.\"},{\"col1\":\"Advising and assisting a private investor in connection with a joint venture arrangement with the Government of Ondo State of Nigeria, involving the privatization of the Ondo State Afforestation Project which was initially funded by the African Development Bank.\"},{\"col1\":\"Advising on the Merger between the Nigerian subsidiaries of SmithKline Beecham and Sterling Products.\"},{\"col1\":\"Conducted legal due diligence and advised Capital Alliance, a foremost international private equity firm in a $15million USD private equity investment in a leading cash-in-transit company in Nigeria.\"},{\"col1\":\"Advising Skye Bank Plc in connection with the negotiation of a trade finance facility from African Export \\u2013 Import Bank (\\u201cAfrexim\\u201d)\"},{\"col1\":\"Advising and assisting a private investor in connection with the acquisition of Iwopin Pulp and Paper Company from the Bureau of Public Enterprises (\\u201cBPE\\u201d) \\/ Federal Government of Nigeria.\"},{\"col1\":\"Advising Inlaks Plc, a Conglomerate at that time, on the separation and corporatisation of some of its division.\"},{\"col1\":\"Advising a leading Aluminum Company in Nigeria on a Scheme of Arrangement involving the Parent Company and a subsidiary.\"}]', '[{\"col1\":\"\"}]', NULL, NULL, NULL, NULL, NULL, NULL, '2015-11-17 12:20:17', '2015-11-17 12:20:17', 'fba72542-4884-4d83-b09c-0d021e5c20c2');
