INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('24', '24', 'en_us', 'Nkoyo Rapu', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Of Counsel', '<p>Nkoyo has over 20 years extensive experience&nbsp;and expertise in general law practice and advocacy.&nbsp; She started her legal career as a government&nbsp;legal representative with the Legal Aid Council and then roceeded to Oshinowo&nbsp;& Co as a Counsel in Chambers before joining the prestigious Allen and&nbsp;Ogunkeye.</p>\r\n\r\n<p>She is also involved in extensive work with&nbsp;non-governmental organizations with particular emphasis on matters relating to&nbsp;women and children in the socio-economic and political context of the Nigerian&nbsp;society. In this regard, she has played a significant role at the national&nbsp;level in the area of women empowerment&nbsp;having been appointed to various&nbsp;committees. She sits on the board of trustees of Freedom Foundation, an&nbsp;umbrella organization of five NGO units committed to pioneering change in&nbsp;society through poverty alleviation and wealth creation. She is also director&nbsp;of Stone Creek Property Investment Company whose aim is to project a new way of&nbsp;community living.</p>\r\n\r\n\r\n\r\n\r\n\r\n<p>Nkoyo has led two&nbsp;trade missions to Togo and more recently, to South Africa with the involvement&nbsp;of both the Nigerian embassy and the embassies of the host countries. She&nbsp;was amongst a select Nigerian&nbsp;delegation appointed by the American Embassy to&nbsp;attend the Annual Business Global&nbsp;Renaissance Summit in Washington D.C, their&nbsp;assignment focus being the creation of more business for more women across more&nbsp;borders. She also served as a board member of the Canadian and African Business&nbsp;Women’s Alliance - (CAABWA).</p>', '[{\"col1\":\"University of Lagos (LLM) \"},{\"col1\":\"The City of London Polytechnic (Diploma in English Private Law).\"}]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '<p><strong>Federal Government Committees:</strong></p><p>Women’s Affirmative Action Committee,&nbsp;December 1998 inaugurated by the Federal Ministry of Women’s Affairs, Abuja,Nigeria.<br><strong>A</strong><strong>ssignment:</strong>&nbsp;Constitutional amendments for women.</p><p>Constitution Drafting Committee, January&nbsp;2000 inaugurated by National Centre for Women’s Development, Abuja.<br><strong>Assignment:</strong>&nbsp;Prepare consensus on position of&nbsp;Nigerian women on 1999 constitution</p><p>American Embassy Nigerian Delegation:<br>Selected to attend the Annual Business Women’s Global Renaissance Summit in&nbsp;Washington D.C, October 1999.<br><strong>Assignment:</strong>&nbsp;More Business for More Women across&nbsp;More Borders.</p><p>Canadian and African Business Women’s&nbsp;Alliance Nigeria (CAABWA)<br><strong>Assignment:</strong>&nbsp;Forge business associations between<br>Nigerian and Canadian Business Women.</p>', '[{\"col1\":\"Norwegian Human Rights Fund Royal Norwegian Embassy Lagos\"},{\"col1\":\"United States Agency Information Development (USAID)\\u00a0Victoria Island, Lagos.\"},{\"col1\":\"Centre for Development and Population Activities (CEDPA) Lagos Chapter\"},{\"col1\":\"Jamaican High Commission, Abuja, Nigeria.\\u00a0\\u00a0\\u00a0\\u00a0\"}]', NULL, '2015-11-17 12:52:33', '2015-11-17 18:11:13', '9f877360-5795-45d3-a039-355039ee897c');
