INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('31', '35', 'en_us', 'The Exclusive Jurisdiction of the National Industrial Court in Labour and Employment Matters', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The National Industrial Court is vested with exclusive jurisdiction on all labour, employment and related matters and as such all labour, employment and allied matters pending before State High Courts across Nigeria are required to be transferred to the National Industrial Court. This position was confirmed in the recent case of Josiah Madu vs. Solus Schall Nigeria Ltd (Unrep.)', NULL, NULL, NULL, '2015-11-17 14:17:08', '2015-11-17 17:29:31', 'afb5901f-6ebc-4af4-a54e-6a7f69ff723b');
