INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('40', '44', 'en_us', 'Ijeoma Abalogu', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Senior Associate', '<p>Ijeoma is responsible for the firm’s&nbsp;corporate compliance and regulatory matters. She has advised several companies&nbsp;on the establishment of business entities in Nigeria, as well as completed&nbsp;registration and obtaining relevant licenses and approvals for corporate&nbsp;clients including Statoil, Baker Hughes, Parker Drilling, Western Atlas and&nbsp;Petro Explorer Ltd. She heads the business advisory and compliance unit in the&nbsp;firm and is also in charge of all company secretarial matters. She also focuses&nbsp;on international investments and immigration practices and procedures, which is&nbsp;a constantly evolving area, with proven client satisfaction.</p><p>Prior to joining GBC Law, Ijeoma had a stint&nbsp;at another law firm where she was involved in the legal&nbsp;advisory and client&nbsp;representations on various aspects of corporate and commercial law.</p>', '[{\"col1\":\"Abia State University, Nigeria (LLB); 2002 \"},{\"col1\":\"The Nigerian Bar Association 2004\"}]', '[{\"col1\":\"Corporate and Commercial Law \"},{\"col1\":\"Legal Advisory\"},{\"col1\":\"Company Secretarial \"},{\"col1\":\"Regulatory Compliance \"},{\"col1\":\"Intellectual Property \"},{\"col1\":\"Business Start-ups \"},{\"col1\":\"International Investments \"},{\"col1\":\"Private Equity \"},{\"col1\":\"Tax\"}]', NULL, '[{\"col1\":\"Participated with the corporate team in the winding-up of the subsidiary of a multi-national oil servicing firm and assisted in ensuring that outstanding (debts) receivables are recovered on behalf of the company.\"},{\"col1\":\"Represented African Capital Alliance, a leading African private equity company, in conducting legal due diligence and structuring a management buy-in culminating in a purchase of equities worth $15 million USD.\"},{\"col1\":\"Acted for a land drilling company in an $18 million private equity investment.\"},{\"col1\":\"Advising and assisting several clients on the establishment of Nigerian subsidiaries and procuring relevant pre-investment and operational approvals, licenses and permits.\"}]', '[{\"col1\":\"\"}]', NULL, NULL, NULL, NULL, NULL, NULL, '2015-11-17 17:01:56', '2015-11-17 17:48:25', '454a20a6-8051-4ef3-b6ba-0108aadda82f');
