INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('62', '66', 'en_us', 'Ayoade Oluwasanmi  ', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Associate', '<p>Ayoade has over 10 years&nbsp;cognate experience advising on and litigating tax, labour, oil and gas, company&nbsp;law and recovery of premises related matters.</p><p>He has a wide experience of the law on a&nbsp;range of issues but has a bias for commercial litigation and arbitration. He&nbsp;has practical, working knowledge in litigation matters on a wide range of&nbsp;issues as diverse as Energy, Employment, Credit Transactions, Commercial&nbsp;Transactions, and Real Estate. He is a member of the litigation team at GBC&nbsp;Law.</p>', '[{\"col1\":\"Ambrose Alli University (LLB); 2004 \"},{\"col1\":\"The Nigerian Bar 2005\"}]', '[{\"col1\":\"Corporate Commercial Litigation \"},{\"col1\":\"Corporate and Commercial Law \"},{\"col1\":\"Legal Advisory\\u00a0\\u00a0\\u00a0\\u00a0\"}]', '[{\"col1\":\"The Nigerian Bar Association \"},{\"col1\":\"The UK Chartered Institute of Arbitrators\"}]', '[{\"col1\":\"Provided representation for an international professional services firm in a N1.4 billion action involving report by it on the issue of the incorrectly reported profit by a publicly quoted company in Nigeria.\"},{\"col1\":\"Represented a multi-national oil company at the Citizens Mediation Centre in a labour related matter filed by its employees.\"},{\"col1\":\"Advised a multi-national oil service company with regards to its liabilities and responsibilities in connection with the environmental impact of the company\\u2019s drilling activities in the Niger-Delta region of Nigeria.\"},{\"col1\":\"Represented Teleglobe in the enforcement of a foreign judgment in Nigeria relating to the termination of Internet Subscription Service Agreement.\"},{\"col1\":\"Was the counsel in charge of the litigation on behalf of Baker Hughes Nigeria Limited, a multinational oil services firm with regards to illegal tax levied against it.\\u00a0 \"},{\"col1\":\"Was part of the team that represented and advised African Capital Alliance, a leading African private equity company, in conducting legal due diligence and structuring a management buy-in culminating in a purchase of majority shares in the dominant cash-in-transit services company in Nigeria.\"},{\"col1\":\"Was part of the litigation team that represented PricewaterhouseCooopers, a multinational financial advisory and auditing firm with regards to the alleged misstatement of profits by Cadbury Nigeria Limited which led to an increased shareholding by Cadbury in the UK.\"},{\"col1\":\"Was part of the team that instituted an action against the Lagos State Government and obtained judgement in favour of a client, obtaining a reversal of a purported revocation of both a Certificate of Occupancy and allocation by the New Towns Development Authority.\"}]', '[{\"col1\":\"\"}]', NULL, NULL, NULL, '', NULL, NULL, '2015-11-17 19:12:29', '2015-11-17 19:12:29', '4df57f85-c23f-42ab-afb9-c0672bc28bec');
