INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('66', '70', 'en_us', 'Edward O. Vera Cruz', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Of Counsel', '<p>Edward’s practice extends over 20 years and has covered a&nbsp;comprehensive range of international corporate and commercial transactions. In&nbsp;particular, he focuses on oil and gas law, law of credit and security,&nbsp;international business transactions law, intellectual property and information&nbsp;technology law.</p><p>In the energy sector, Edward has acted for sponsors and a&nbsp;wide variety of other clients in major project finance, general corporate and&nbsp;commercial transactions and regulatory matters.</p><p>Edward has held various leadership positions in most of the&nbsp;firms he has worked with. He was the Head of Chambers in the Firm of Lanre&nbsp;Olaoluwa & Co. (now Matrix Solicitors) and later Managing Partner in the&nbsp;Firm of Victor and Charles Solicitors (now part of AELEX Partners), a position&nbsp;he held for over 7 years.</p><p> He is a regular participant at local and international&nbsp;seminars and was for a number of years both a visiting lecturer on the Nigerian&nbsp;Institute of Advanced Legal Studies \'Advance Legal Drafting Course\' and the&nbsp;contributing rapporteur for Nigeria for the Journal of Energy and Natural&nbsp;Resources Law.</p>', '[{\"col1\":\"University of Lagos (LLB) \"},{\"col1\":\"The Nigerian Bar Association 1990\"},{\"col1\":\"Solicitor of the Supreme Court of England and Wales\"},{\"col1\":\"Solicitor of the Supreme Court of England and Wales\"}]', '[{\"col1\":\"Oil and Gas\\u00a0\"},{\"col1\":\"Energy\\u00a0\"},{\"col1\":\"International Trade\\u00a0\"},{\"col1\":\"Public Procurement\\u00a0\"},{\"col1\":\"Project Finance \"}]', '[{\"col1\":\"Solicitor of the Supreme Court of England and Wales\"},{\"col1\":\"The Law Society of England & Wales \"},{\"col1\":\"The Procurement Lawyers Association \"},{\"col1\":\"The Association of British Council Fellows \"},{\"col1\":\"The Association of British Chevening Scholars  \"},{\"col1\":\"Gideons International\"}]', '[{\"col1\":\"Representing Texaco Inc., and providing general legal advice on the implementation in Nigeria of an incorporated joint venture between Chevron and Texaco for the marketing internationally of marine fuels and lubricants.\"},{\"col1\":\"Representing ENRON International; Enron B\\u00e9nin Gas Holdings Ltd; and Enron West Africa Pipeline Ltd and providing general legal advisory work and consultations on the structuring, contract documentation, and security issues on the acquisition by Enron of the rights to all of the gas and condensate in the Aje Field of OML 113 and future rights in the gas and condensate produced from OML 113 outside the Aje Field.\"},{\"col1\":\"Rendered advice to TOTAL Exploration Nig. Ltd. on the legal aspects of the proposed acquisition by TOTAL (Transferee) of the interests of Ashland Nigeria Exploration Unlimited in various Oil Producing Licences in Nigeria.\"},{\"col1\":\"Advising and documentation for TOTAL Upstream Nigeria Ltd on the unincorporated joint venture, with a local and another international party, for the joint prospecting for and exploitation of the oil deposits in a virgin offshore concession (OPL 246).\"},{\"col1\":\"Preparing Farm-In Agreement involving TOTAL Upstream Nigeria Ltd on the acquisition by TOTAL (Farminee) of 24% undivided interest in the deep offshore field (OPL 246) granted to South Atlantic Petroleum Ltd.\"},{\"col1\":\"Completed legal, regulatory and due diligence aspects of the acquisition by Petroleum Oil & Gas Company of South Africa (Nig.) Ltd PETROSA (Nigeria) (Transferee) of 50% (non-operating) interest in the existing production fields of Addax Petroleum Nigeria.\"},{\"col1\":\"One of the Legal Advisers (Nigeria) to the Project Sponsors\\/Commercial Group, including Chevron, Shell, Nigeria National Petroleum Corporation, SobeGaz, SotoGaz,and the Ghanaian National Petroleum Corporation, which included Project structuring, project financing, and environmental issues advise on the West African sub-regional gas pipeline project.\"},{\"col1\":\"Team member: pre-project advisory team to Mobil Power Inc. and Mobil Producing Nigeria Unlimited (Project Sponsor) \\u2013 Preliminary, behind-the-scenes advisory work and consultations on the statutory and regulatory framework existing in Nigeria for the setting up of an independent natural gas-based power. \"}]', '[{\"col1\":\"\"}]', NULL, NULL, NULL, '', NULL, NULL, '2015-11-17 19:43:41', '2015-11-17 19:43:41', 'fd3eb3db-feca-4d3b-a2e8-2effc3599e72');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('68', '72', 'en_us', 'Lola Ogunmodede', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Partner', '<p>Lola&nbsp;has over 19 years of hands-on experience in corporate/commercial law. She has&nbsp;extensive experience and expertise in handling large-scale commercial&nbsp;transactions particularly involving the banking and financial services industry.&nbsp;She has worked with First Atlantic Bank Limited as The Legal Adviser/Company<br> Secretary and in the legal department of Union Bank where she advised on a&nbsp;variety of legal matters.&nbsp; Whilst in these organisations, she was involved&nbsp;in the negotiation and documentation of several banking transactions and the&nbsp;drafting of various commercial contracts such as the acquisition of a&nbsp;merchant-banking subsidiary by Union Bank.</p>', '[{\"col1\":\"Ogun State University (LLB); 1990\"},{\"col1\":\"Nigerian Bar Association 1991. \"},{\"col1\":\"University of Lagos (Masters in Law) \"},{\"col1\":\"IESE, University of Navarra, Barcelona, Spain (MBA). \"}]', '[{\"col1\":\"Trade and Investments\"},{\"col1\":\"Corporate Law \"},{\"col1\":\"Banking and Finance \"},{\"col1\":\"Employment \"},{\"col1\":\"Commercial Contracts \"},{\"col1\":\"Company Secretarial Practice\"},{\"col1\":\"Project Finance\"},{\"col1\":\"Mergers and Acquisitions\"}]', '[{\"col1\":\"Nigerian Bar Association\"}]', '[{\"col1\":\"Advising on the acquisition of a merchant-banking subsidiary by Union Bank of Nigeria PLC.\"},{\"col1\":\"Advising on the acquisition of\\u00a0Flexcube\\u00a0banking software by Union Bank of Nigeria Plc and Finnacle\\u00a0banking software by First Atlantic Bank Ltd.\"},{\"col1\":\"Advising on the acquisition of an Internet Based Money Transfer facility by First Atlantic Bank Ltd.\"},{\"col1\":\"Advising on the setting up of a Staff Share Investment scheme by First Atlantic Bank Ltd.\"},{\"col1\":\"Advising and documenting various syndicated facilities relating to Peugeot Automobile Nigeria, Intel Oil Services, G. Cappa, International Distillers, Victoria Garden Hotels and several other leading companies.\"},{\"col1\":\"Drafting, reviewing and advising on various Security Documentation including but not limited to Legal Mortgages, Debentures (Fixed, Floating and Mortgage), Equipment Leases including Sale and Lease Back contracts, pledges, guarantees and indemnities.\"},{\"col1\":\"Conducting market research and putting together the business plan of\\u00a0Venture Partners\\u00a0a start up Venture Capital Company focused on offering financial and managerial support to small and medium scale enterprises.\"}]', '[{\"col1\":\"\"}]', NULL, NULL, NULL, '<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Advising on the acquisition of a<br> merchant-banking subsidiary by Union Bank of Nigeria PLC.</p>', NULL, '[{\"col1\":\"English and French\"}]', '2015-11-17 19:51:49', '2015-11-17 20:11:28', '864d3591-95fa-47fe-86c4-f07744ab26a8');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('69', '73', 'en_us', 'Wole Tejuoso', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Partner', '<p>Wole is responsible for intellectual property matters at GBC&nbsp;Law.&nbsp; He also focuses on real estate transactions, and corporate&nbsp;commercial law.&nbsp; He has over 19 years experience of legal practice and&nbsp;also has considerable expertise in e-commerce. He has also been involved in a&nbsp;number of transactions in the Nigerian energy sector.</p>\r\n\r\n<p> <br> Wole worked as an associate at Sheikh & Co. Solicitors,&nbsp;London, before joining Merrill Lynch International Bank, London where he worked&nbsp;in the Private Banking Department. He worked as Company Secretary at Dabis&nbsp;International Company Limited, London also, before a stint at Irving &&nbsp;Bonnar where he worked with the corporate/commercial group on a variety of&nbsp;transactions for both domestic and international clients.</p>', '[{\"col1\":\"Lagos State University (LLB); 1990 \"},{\"col1\":\"The Nigerian Bar 1991 \"},{\"col1\":\"The Washington College of Law (Masters in Law) \"},{\"col1\":\"He qualified as a Solicitor in England and Wales in 1999. \"},{\"col1\":\"He qualified as a Solicitor in England and Wales in 1999. \"}]', '[{\"col1\":\"Intellectual Property \"},{\"col1\":\"Energy\"},{\"col1\":\"Banking and Finance \"},{\"col1\":\"Banking and Finance \"},{\"col1\":\"Real Estate\"}]', '[{\"col1\":\"He qualified as a Solicitor in England and Wales in 1999. \"},{\"col1\":\"The International Trademark Association (INTA).\"}]', NULL, '[{\"col1\":\"\"}]', NULL, NULL, NULL, '', NULL, NULL, '2015-11-17 19:57:07', '2015-11-17 20:28:08', '999e410b-525c-4843-af78-ceb0cc00229b');
