INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_seoTitle`, `field_seoAuthor`, `field_seoCanonical`, `field_seoDescription`, `field_seoKeywords`, `field_seoType`, `field_aboutText`, `field_bodyContent`, `field_clientsText`, `field_position`, `field_intro`, `field_education`, `field_expertise`, `field_memberships`, `field_experiences`, `field_awards`, `field_relevantExperiences`, `field_relevantExperiencesIntro`, `field_summary`, `field_appointments`, `field_consultancyRecord`, `field_languages`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('74', '78', 'en_us', 'Edwin Egede', NULL, '', '', '', '', '', '', NULL, NULL, NULL, 'Consultant', '<p>Dr.&nbsp;Egede (PhD. International Law) has over 25 years extensive practice experience.&nbsp;His expertise covers a range of subjects, including International Maritime and&nbsp;Trade Law, Foreign Investment Law, Enforcement of Foreign Judgments, Oil and&nbsp;Gas Law, Bilateral Investment Treaties (BITs) claims, State and Diplomatic&nbsp;<br> immunity Law and the Law on the Regulation and Management of Offshore Mineral&nbsp;and Fisheries and all aspects of International Law. Over the years he has acted&nbsp;and /or provided advice to various Multinational Corporations (MNCs), Banks,&nbsp;Governments and Government agencies. He has provided advice in respect of the&nbsp;recognition and enforcement of the judgment of the Court of a Latin American&nbsp;Country in Nigeria and the application of the United States Foreign Corrupt&nbsp;Practices Act (FCPA) in Nigeria. He has also advised a State government in&nbsp;Nigeria on its rights under international law to the offshore sea-bed and the<br> resources therein. Further, he has provided advice on the impact of the<br> European Union Common Fisheries Policy.</p><p>Dr.&nbsp;Egede is currently engaged in interdisciplinary teaching and research in&nbsp;International Law and International Relations at the Cardiff University and is&nbsp;a Fellow of the Higher Education Academy (FHEA), United Kingdom.&nbsp;</p><p>He&nbsp;has written and published books with renowned publishers and journal articles&nbsp;in peer reviewed international journals in the area of International Law.</p><p>Dr.&nbsp;Egede is a regular participant at local and international Conferences/Seminars&nbsp;and has delivered papers in various places, including the International&nbsp;Hydrographic Organisation/International Association of Geodesy Advisory Board&nbsp;on the Law of the Sea (ABLOS) Conference in Monaco; the African Studies&nbsp;Association of the UK (ASAUK) Conference at the Oxford University and the&nbsp;Institute for International Shipping and Trade Law, Swansea University.</p>', '[{\"col1\":\"University of Benin (LLB Hons.) \"},{\"col1\":\"University of Lagos (LLM) \"},{\"col1\":\"Cardiff University, Cardiff, Wales, United Kingdom (PhD in International Law) \"}]', '[{\"col1\":\"International Law \"},{\"col1\":\"International Maritime and Trade Law \"},{\"col1\":\"Foreign Investment Law \"},{\"col1\":\"Enforcement of Foreign Judgments \"},{\"col1\":\"Oil and Gas Law \"},{\"col1\":\"Bilateral Investment Treaties \"},{\"col1\":\"State and Diplomatic Immunity Law\"}]', '[{\"col1\":\"Nigerian Bar Association \"},{\"col1\":\"The International Law Association \"},{\"col1\":\"The Academic Council on the United Nations Systems \"},{\"col1\":\"The Legal, Economic & Regulatory Section of Gerson Lehrman Group \"},{\"col1\":\"The Welsh Centre of International Affairs (WCIA) \"},{\"col1\":\"Legal Affairs Committee, Wales, United Kingdom. \"}]', NULL, '[{\"col1\":\"\"}]', NULL, NULL, NULL, '', NULL, NULL, '2015-11-17 20:17:48', '2015-11-17 20:17:48', '70e7cfee-9ee6-4675-adc6-dd8e0e177347');



