INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('91', '35', '11', '1', 'en_us', '2', '', '{\"typeId\":\"13\",\"authorId\":\"1\",\"title\":\"The Exclusive Jurisdiction of the National Industrial Court in Labour and Employment Matters\",\"slug\":\"the-exclusive-jurisdiction-of-the-national-industrial-court-in-labour-and-employment-matters\",\"postDate\":1447769820,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"33\":[\"31\"],\"32\":\"The National Industrial Court is vested with exclusive jurisdiction on all labour, employment and related matters and as such all labour, employment and allied matters pending before State High Courts across Nigeria are required to be transferred to the National Industrial Court. This position was confirmed in the recent case of Josiah Madu vs. Solus Schall Nigeria Ltd (Unrep.)\",\"2\":[\"45\",\"46\"]}}', '2015-11-17 17:29:32', '2015-11-17 17:29:32', 'dcc3551f-e7b2-45fc-be32-d8b7f4b36a88');
INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('92', '36', '11', '1', 'en_us', '2', '', '{\"typeId\":\"13\",\"authorId\":\"1\",\"title\":\"Arbitrability of tax issues arising from production sharing contracts\\u2013 FIRS v. NNPC\",\"slug\":\"arbitrability-of-tax-issues-arising-from-production-sharing-contracts-firs-v-nnpc\",\"postDate\":1447769880,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"33\":[\"33\"],\"32\":\"The Federal High Court, Abuja in the case of Federal Inland Revenue Service VS. Nigerian National Petroleum Corporation & Others recently decided that an Arbitration Tribunal lacks jurisdiction to determine issues relating to taxation or connected with the Federal Government revenue as such jurisdiction is exclusively conferred on the FHC by the Constitution of the Federal Republic of Nigeria.\",\"2\":[\"47\",\"48\",\"49\"]}}', '2015-11-17 17:31:00', '2015-11-17 17:31:00', 'ce56e905-8991-44b4-95e7-0dfd207f6b48');
